   249â†’// ============================================================================
   250â†’// TEXT EDITOR TOGGLE
   251â†’// ============================================================================
   252â†’function initTextEditorToggle() {
   253â†’    const toggle = document.getElementById('textEditorToggle');
   254â†’    const panel = document.getElementById('text-editor-panel');
   255â†’    console.log('[SPLICE] initTextEditorToggle - toggle:', !!toggle, 'panel:', !!panel);
   256â†’
   257â†’    if (toggle && panel) {
   258â†’        toggle.addEventListener('click', (e) => {
   259â†’            e.preventDefault();
   260â†’            e.stopPropagation();
   261â†’            console.log('[SPLICE] Text Editor toggle clicked');
   262â†’            setStatus('Text Editor: ' + (panel.classList.contains('collapsed') ? 'Opening' : 'Closing'));
   263â†’            // Check state BEFORE toggling
   264â†’            const wasCollapsed = panel.classList.contains('collapsed');
   265â†’            panel.classList.toggle('collapsed');
   266â†’            const icon = toggle.querySelector('.toggle-icon');
   267â†’            console.log('[SPLICE] wasCollapsed:', wasCollapsed, 'icon:', !!icon);
   268â†’            if (icon) {
   269â†’                // If it was collapsed, it's now expanded (show -)
   270â†’                icon.textContent = wasCollapsed ? '-' : '+';
   271â†’            }
   272â†’        });
   273â†’    } else {
   274â†’        console.error('[SPLICE] Text Editor toggle elements not found!');
   275â†’    }
   276â†’}
