   328â†’// ============================================================================
   329â†’// SLIDERS
   330â†’// ============================================================================
   331â†’function initSliders() {
   332â†’    // Sensitivity slider
   333â†’    if (ui.sensitivitySlider && ui.sensitivityValue) {
   334â†’        ui.sensitivitySlider.addEventListener('input', () => {
   335â†’            ui.sensitivityValue.textContent = ui.sensitivitySlider.value;
   336â†’            saveSettings({ sensitivity: parseInt(ui.sensitivitySlider.value) });
   337â†’        });
   338â†’    }
   339â†’
   340â†’    // J-Cut sliders
   341â†’    if (ui.jcutLeadIn && ui.jcutLeadInValue) {
   342â†’        ui.jcutLeadIn.addEventListener('input', () => {
   343â†’            const val = (ui.jcutLeadIn.value / 100).toFixed(2);
   344â†’            ui.jcutLeadInValue.textContent = val + 's';
   345â†’            saveSettings({ jcutLeadIn: parseFloat(val) });
   346â†’        });
   347â†’    }
   348â†’
   349â†’    if (ui.jcutLeadOut && ui.jcutLeadOutValue) {
   350â†’        ui.jcutLeadOut.addEventListener('input', () => {
   351â†’            const val = (ui.jcutLeadOut.value / 100).toFixed(2);
   352â†’            ui.jcutLeadOutValue.textContent = val + 's';
   353â†’            saveSettings({ jcutLeadOut: parseFloat(val) });
   354â†’        });
   355â†’    }
   356â†’}
   357â†’
   358â†’// ============================================================================
   359â†’// FEATURE TOGGLES
   360â†’// ============================================================================
   361â†’function initFeatureToggles() {
   362â†’    // J-Cut toggle
   363â†’    if (ui.enableJCut && ui.jcutSettings) {
   364â†’        ui.enableJCut.addEventListener('change', () => {
   365â†’            ui.jcutSettings.classList.toggle('collapsed', !ui.enableJCut.checked);
   366â†’            saveSettings({ enableJCut: ui.enableJCut.checked });
   367â†’        });
   368â†’    }
   369â†’
   370â†’    // Zoom toggle
   371â†’    if (ui.enableZoom && ui.zoomSettings) {
   372â†’        ui.enableZoom.addEventListener('change', () => {
   373â†’            ui.zoomSettings.classList.toggle('collapsed', !ui.enableZoom.checked);
   374â†’            saveSettings({ enableZoom: ui.enableZoom.checked });
   375â†’        });
   376â†’    }
   377â†’
   378â†’    // Chapters toggle
   379â†’    if (ui.enableChapters && ui.chapterSettings) {
   380â†’        ui.enableChapters.addEventListener('change', () => {
   381â†’            ui.chapterSettings.classList.toggle('collapsed', !ui.enableChapters.checked);
   382â†’            saveSettings({ enableChapters: ui.enableChapters.checked });
   383â†’        });
   384â†’    }
   385â†’
   386â†’    // Takes detection toggle
   387â†’    if (ui.enableTakesDetection) {
   388â†’        ui.enableTakesDetection.addEventListener('change', () => {
   389â†’            saveSettings({ enableTakesDetection: ui.enableTakesDetection.checked });
   390â†’        });
   391â†’    }
   392â†’
   393â†’    // Profanity detection toggle
   394â†’    if (ui.enableProfanity && ui.profanitySettings) {
   395â†’        ui.enableProfanity.addEventListener('change', () => {
   396â†’            ui.profanitySettings.classList.toggle('collapsed', !ui.enableProfanity.checked);
   397â†’            saveSettings({ enableProfanity: ui.enableProfanity.checked });
   398â†’        });
   399â†’    }
   400â†’
   401â†’    // Filler word detection toggle
   402â†’    if (ui.enableFillerDetection && ui.fillerSettings) {
   403â†’        ui.enableFillerDetection.addEventListener('change', () => {
   404â†’            ui.fillerSettings.classList.toggle('collapsed', !ui.enableFillerDetection.checked);
   405â†’            saveSettings({ enableFillerDetection: ui.enableFillerDetection.checked });
   406â†’        });
   407â†’    }
   408â†’}
   409â†’
   410â†’// ============================================================================
   411â†’// PRESET SELECTOR
   412â†’// ============================================================================
   413â†’// PRESETS is defined in config.js
   414â†’
   415â†’function initPresetSelector() {
   416â†’    if (!ui.presetSelector) return;
   417â†’
   418â†’    ui.presetSelector.addEventListener('change', () => {
   419â†’        const presetId = ui.presetSelector.value;
   420â†’        applyPreset(presetId);
   421â†’    });
   422â†’
   423â†’    // Load current preset
   424â†’    const settings = getSettings();
   425â†’    if (settings.activePreset) {
   426â†’        ui.presetSelector.value = settings.activePreset;
   427â†’    }
   428â†’}
   429â†’
   430â†’function applyPreset(presetId) {
   431â†’    const preset = PRESETS[presetId];
   432â†’    if (!preset) return;
   433â†’
   434â†’    // Custom preset - keep current settings
   435â†’    if (presetId === 'custom' || !preset.settings) {
   436â†’        saveSettings({ activePreset: 'custom' });
   437â†’        setStatus('Using custom settings');
   438â†’        return;
   439â†’    }
   440â†’
   441â†’    const settings = preset.settings;
   442â†’
   443â†’    // Apply to UI
   444â†’    if (ui.sensitivitySlider && settings.sensitivity !== undefined) {
   445â†’        ui.sensitivitySlider.value = settings.sensitivity;
   446â†’        if (ui.sensitivityValue) ui.sensitivityValue.textContent = settings.sensitivity;
   447â†’    }
   448â†’
   449â†’    if (ui.enableJCut) {
   450â†’        ui.enableJCut.checked = settings.enableJCut || false;
   451â†’        if (ui.jcutSettings) {
   452â†’            ui.jcutSettings.classList.toggle('collapsed', !settings.enableJCut);
   453â†’        }
   454â†’    }
   455â†’
   456â†’    if (settings.jCutLeadIn !== undefined && ui.jcutLeadIn) {
   457â†’        ui.jcutLeadIn.value = Math.round(settings.jCutLeadIn * 100);
   458â†’        if (ui.jcutLeadInValue) ui.jcutLeadInValue.textContent = settings.jCutLeadIn + 's';
   459â†’    }
   460â†’
   461â†’    if (settings.jCutLeadOut !== undefined && ui.jcutLeadOut) {
   462â†’        ui.jcutLeadOut.value = Math.round(settings.jCutLeadOut * 100);
   463â†’        if (ui.jcutLeadOutValue) ui.jcutLeadOutValue.textContent = settings.jCutLeadOut + 's';
   464â†’    }
   465â†’
   466â†’    // Save merged settings
   467â†’    saveSettings({
   468â†’        activePreset: presetId,
   469â†’        sensitivity: settings.sensitivity,
   470â†’        threshold: settings.threshold,
   471â†’        minSilenceLength: settings.minSilenceLength,
   472â†’        paddingStart: settings.paddingStart,
   473â†’        paddingEnd: settings.paddingEnd,
   474â†’        enableTakesDetection: settings.enableTakesDetection,
   475â†’        enableJCut: settings.enableJCut || false,
   476â†’        jcutLeadIn: settings.jCutLeadIn || 0.3,
   477â†’        jcutLeadOut: settings.jCutLeadOut || 0.2
   478â†’    });
   479â†’
   480â†’    setStatus(`Applied ${preset.name} preset`);
   481â†’}
   482â†’
   483â†’// ============================================================================
   484â†’// LOAD SETTINGS TO UI
   485â†’// ============================================================================
   486â†’function loadSettingsToUI() {
   487â†’    const settings = getSettings();
   488â†’
   489â†’    if (ui.sensitivitySlider) {
   490â†’        ui.sensitivitySlider.value = settings.sensitivity;
   491â†’        if (ui.sensitivityValue) ui.sensitivityValue.textContent = settings.sensitivity;
   492â†’    }
   493â†’
   494â†’    if (ui.enableTakesDetection) ui.enableTakesDetection.checked = settings.enableTakesDetection;
   495â†’    if (ui.enableJCut) ui.enableJCut.checked = settings.enableJCut;
   496â†’    if (ui.enableZoom) ui.enableZoom.checked = settings.enableZoom;
   497â†’    if (ui.enableChapters) ui.enableChapters.checked = settings.enableChapters;
   498â†’
   499â†’    if (ui.jcutSettings) ui.jcutSettings.classList.toggle('collapsed', !settings.enableJCut);
   500â†’    if (ui.zoomSettings) ui.zoomSettings.classList.toggle('collapsed', !settings.enableZoom);
   501â†’    if (ui.chapterSettings) ui.chapterSettings.classList.toggle('collapsed', !settings.enableChapters);
   502â†’
   503â†’    if (ui.jcutLeadIn) {
   504â†’        ui.jcutLeadIn.value = Math.round(settings.jcutLeadIn * 100);
   505â†’        if (ui.jcutLeadInValue) ui.jcutLeadInValue.textContent = settings.jcutLeadIn + 's';
   506â†’    }
   507â†’    if (ui.jcutLeadOut) {
   508â†’        ui.jcutLeadOut.value = Math.round(settings.jcutLeadOut * 100);
   509â†’        if (ui.jcutLeadOutValue) ui.jcutLeadOutValue.textContent = settings.jcutLeadOut + 's';
   510â†’    }
   511â†’
   512â†’    if (ui.presetSelector && settings.activePreset) {
   513â†’        ui.presetSelector.value = settings.activePreset;
   514â†’    }
   515â†’}
   516â†’
   517â†’// ============================================================================
   518â†’// GO BUTTON - MAIN WORKFLOW
   519â†’// ============================================================================
   520â†’function initGoButton() {
   521â†’    if (!ui.goBtn) return;
