   981â†’async function buildSequence() {
   982â†’    if (isOperationInProgress) return;
   983â†’
   984â†’    isOperationInProgress = true;
   985â†’    setStatus('Building sequence...');
   986â†’
   987â†’    try {
   988â†’        const settings = getSettings();
   989â†’
   990â†’        // Ensure we have sequence info
   991â†’        if (!currentSequenceInfo) {
   992â†’            currentSequenceInfo = await jsx.call('getActiveSequence');
   993â†’        }
   994â†’
   995â†’        // Create cut list from selected silences
   996â†’        const selectedSilences = previewSilences.filter((_, i) => selectedSilenceIndices.has(i));
   997â†’
   998â†’        // Calculate total duration from sequence info or last silence
   999â†’        let duration = 0;
  1000â†’        if (currentSequenceInfo && currentSequenceInfo.duration) {
  1001â†’            duration = currentSequenceInfo.duration;
  1002â†’        } else if (previewSilences.length > 0) {
  1003â†’            const lastSilence = previewSilences[previewSilences.length - 1];
  1004â†’            duration = lastSilence.end + 10; // Add buffer
  1005â†’        }
  1006â†’
  1007â†’        // Call backend to generate cut list
  1008â†’        const cutListResponse = await fetchWithTimeout(
  1009â†’            `${getBackendUrl()}/cut-list`,
  1010â†’            {
  1011â†’                method: 'POST',
  1012â†’                headers: getAuthHeaders(),
  1013â†’                body: JSON.stringify({
  1014â†’                    sourceName: currentSequenceInfo?.name || 'Untitled Sequence',
  1015â†’                    sourcePath: currentSequenceInfo?.treePath || currentSequenceInfo?.name || 'Untitled',
  1016â†’                    duration: duration,
  1017â†’                    silences: selectedSilences,
  1018â†’                    takes: previewTakes,
  1019â†’                    enableTakes: settings.enableTakesDetection,
  1020â†’                    jCutOffset: settings.enableJCut ? settings.jcutLeadIn : 0,
  1021â†’                    lCutOffset: settings.enableJCut ? settings.jcutLeadOut : 0
  1022â†’                })
  1023â†’            }
  1024â†’        );
  1025â†’
  1026â†’        if (!cutListResponse.ok) {
  1027â†’            throw new Error(await parseErrorResponse(cutListResponse));
  1028â†’        }
  1029â†’
  1030â†’        const cutList = await cutListResponse.json();
  1031â†’
  1032â†’        // Build sequence via JSX
  1033â†’        const result = await jsx.call('buildSequenceFromCutList', JSON.stringify(cutList));
  1034â†’
  1035â†’        if (result && result.success) {
  1036â†’            setStatus(`Built sequence: ${result.sequenceName}`);
  1037â†’            hidePreview();
  1038â†’        } else {
  1039â†’            throw new Error(result?.error || 'Build failed');
  1040â†’        }
  1041â†’    } catch (error) {
  1042â†’        setStatus('Build failed: ' + error.message, true);
  1043â†’    } finally {
  1044â†’        isOperationInProgress = false;
  1045â†’    }
  1046â†’}
