   872â†’function startPollingVariations(jobId) {
   873â†’  // Clear any existing polling
   874â†’  if (musicState.variationsPollingInterval) {
   875â†’    clearInterval(musicState.variationsPollingInterval);
   876â†’  }
   877â†’
   878â†’  // Track which variations job we're polling for (race condition fix)
   879â†’  musicState.currentVariationsJobId = jobId;
   880â†’
   881â†’  musicState.variationsPollingInterval = setInterval(async () => {
   882â†’    // Race condition check: verify we're still polling for this job
   883â†’    if (musicState.currentVariationsJobId !== jobId) {
   884â†’      return; // A new variations job started polling, ignore this callback
   885â†’    }
   886â†’
   887â†’    try {
   888â†’      const status = await getVariationsStatus(jobId);
   889â†’
   890â†’      // Double-check after async operation
   891â†’      if (musicState.currentVariationsJobId !== jobId) {
   892â†’        return;
   893â†’      }
   894â†’
   895â†’      musicState.variationsJob = { ...musicState.variationsJob, ...status };
   896â†’      renderVariationsProgress(status);
   897â†’
   898â†’      if (status.status === 'selecting' || status.status === 'completed') {
   899â†’        clearInterval(musicState.variationsPollingInterval);
   900â†’        musicState.variationsPollingInterval = null;
   901â†’        musicState.currentVariationsJobId = null;
   902â†’
   903â†’        if (status.variations && status.variations.length > 0) {
   904â†’          setMusicStatus('Variations ready! Select your favorite.', 'success');
   905â†’          renderVariationsSelection(status.variations);
   906â†’        }
   907â†’      } else if (status.status === 'failed') {
   908â†’        clearInterval(musicState.variationsPollingInterval);
   909â†’        musicState.variationsPollingInterval = null;
   910â†’        musicState.currentVariationsJobId = null;
   911â†’        setMusicStatus(`Variations failed: ${status.failedReason || 'Unknown error'}`, 'error');
   912â†’      }
   913â†’    } catch (error) {
   914â†’      console.error('Variations polling error:', error);
   915â†’    }
   916â†’  }, MUSIC_POLL_INTERVAL);
   917â†’}
