  1113â†’function startPollingJob(jobId) {
  1114â†’  // Clear any existing polling
  1115â†’  if (musicState.pollInterval) clearInterval(musicState.pollInterval);
  1116â†’
  1117â†’  // Track which job we're now polling for (race condition fix)
  1118â†’  musicState.currentPollingJobId = jobId;
  1119â†’
  1120â†’  musicState.pollInterval = setInterval(async () => {
  1121â†’    // Race condition check: verify we're still polling for this job
  1122â†’    if (musicState.currentPollingJobId !== jobId) {
  1123â†’      return; // A new job started polling, ignore this callback
  1124â†’    }
  1125â†’
  1126â†’    try {
  1127â†’      const status = await getJobStatus(jobId);
  1128â†’
  1129â†’      // Double-check after async operation
  1130â†’      if (musicState.currentPollingJobId !== jobId) {
  1131â†’        return;
  1132â†’      }
  1133â†’
  1134â†’      const jobIndex = musicState.jobs.findIndex(j => j.jobId === jobId);
  1135â†’      if (jobIndex !== -1) {
  1136â†’        musicState.jobs[jobIndex] = { ...musicState.jobs[jobIndex], ...status };
  1137â†’        renderJobsList();
  1138â†’      }
  1139â†’
  1140â†’      if (status.status === 'completed' || status.status === 'failed') {
  1141â†’        clearInterval(musicState.pollInterval);
  1142â†’        musicState.pollInterval = null;
  1143â†’        musicState.currentPollingJobId = null;
  1144â†’        if (status.status === 'completed') {
  1145â†’          setMusicStatus('Music generation completed!', 'success');
  1146â†’          loadMusicLibrary();
  1147â†’        } else {
  1148â†’          setMusicStatus(`Generation failed: ${status.failedReason || 'Unknown error'}`, 'error');
  1149â†’        }
  1150â†’      }
  1151â†’    } catch (error) {
  1152â†’      console.error('Polling error:', error);
  1153â†’    }
  1154â†’  }, MUSIC_POLL_INTERVAL);
  1155â†’}
