      minChapterLength,
      crossfadeDuration,
      instruments: instruments ? [instruments] : [],
      prompt
    });

    setMusicStatus(`Timeline generated! ${result.chapters?.length || 0} chapters, ${formatDuration(result.duration)}`, 'success');

