   413â†’
   414â†’/**
   415â†’ * Render preview of changes
   416â†’ */
   417â†’function renderPreview(preview) {
   418â†’  const container = textEditorElements.previewContainer || document.getElementById('text-editor-preview');
   419â†’  if (!container) return;
