   421â†’  if (!preview.changes || preview.changes.length === 0) {
   422â†’    container.innerHTML = '<div class="preview-empty">No changes detected</div>';
   423â†’    return;
   424â†’  }
   425â†’
   426â†’  // SECURITY: Escape user-provided content to prevent XSS
   427â†’  const html = `
   428â†’    <div class="preview-summary">
   429â†’      <span class="preview-stat">${escapeHtml(preview.summary.deletions)} deletions</span>
   430â†’      <span class="preview-stat">${escapeHtml(preview.summary.reorderings)} reorders</span>
