   441â†’    </div>
   442â†’  `;
   443â†’
   444â†’  container.innerHTML = html;
   445â†’}
   446â†’
   447â†’/**
   448â†’ * Apply text edits and generate cut list
   449â†’ */
   450â†’async function applyTextEdits() {
   451â†’  if (!textEditorState.editableTranscript || !textEditorState.isDirty) {
   452â†’    showTextEditorStatus('No changes to apply', 'info');
   453â†’    return;
   454â†’  }
   455â†’
   456â†’  textEditorState.isProcessing = true;
   457â†’  updateProcessingState(true);
   458â†’  showTextEditorStatus('Applying edits...', 'info');
   459â†’
   460â†’  try {
   461â†’    const backendUrl = typeof getBackendUrl === 'function' ? getBackendUrl() : 'https://127.0.0.1:3847';
   462â†’    const headers = typeof getAuthHeaders === 'function' ? getAuthHeaders() : { 'Content-Type': 'application/json' };
   463â†’    const fetchFn = typeof fetchWithTimeout === 'function' ? fetchWithTimeout : fetch;
   464â†’
   465â†’    // Apply edits
   466â†’    const applyResponse = await fetchFn(`${backendUrl}/text-edit/apply`, {
   467â†’      method: 'POST',
   468â†’      headers,
   469â†’      body: JSON.stringify({
   470â†’        transcript: textEditorState.editableTranscript,
   471â†’        editedText: textEditorState.currentText
   472â†’      })
   473â†’    });
   474â†’
   475â†’    if (!applyResponse.ok) {
   476â†’      const err = await applyResponse.json();
   477â†’      throw new Error(err.error || 'Apply failed');
   478â†’    }
   479â†’
   480â†’    const applyData = await applyResponse.json();
   481â†’
   482â†’    // Generate cut list from operations
   483â†’    const cutListResponse = await fetchFn(`${backendUrl}/text-edit/cut-list`, {
   484â†’      method: 'POST',
   485â†’      headers,
   486â†’      body: JSON.stringify({
   487â†’        operations: applyData.operations,
   488â†’        sourceInfo: {
