   275â†’async function generateCaptions() {
   276â†’    const transcript = getLastTranscript();
   277â†’    if (!transcript) {
   278â†’        showCaptionStatus('No transcript available. Run transcription first.', 'error');
   279â†’        return;
   280â†’    }
   281â†’
   282â†’    // Check online status
   283â†’    if (typeof isOnline === 'function' && !isOnline()) {
   284â†’        showCaptionStatus('Offline - Check your connection', 'error');
   285â†’        return;
