   451â†’            };
   452â†’        }
   453â†’
   454â†’        const response = await fetchWithTimeout(`${apiUrl}${endpoint}`, {
   455â†’            method: 'POST',
   456â†’            headers: getAuthHeaders(),
   457â†’            body: JSON.stringify(body)
   458â†’        }, 60000);
   459â†’
   460â†’        if (!response.ok) {
   461â†’            const errorMsg = await parseErrorResponse(response);
   462â†’            throw new Error(errorMsg);
   463â†’        }
   464â†’
   465â†’        const data = await response.json();
   466â†’
   467â†’        if (format === 'mogrt') {
   468â†’            downloadJSON(data.data, 'animated-captions-mogrt.json');
   469â†’            showCaptionStatus('MOGRT data exported', 'success');
   470â†’        } else {
   471â†’            downloadText(data.content, `captions.${format}`);
   472â†’            showCaptionStatus(`${format.toUpperCase()} exported`, 'success');
   473â†’        }
   474â†’
   475â†’    } catch (err) {
   476â†’        console.error('[SPLICE Captions] Export error:', err);
   477â†’        showCaptionStatus(err.message, 'error');
   478â†’    }
   479â†’}
   480â†’
   481â†’function flattenCaptionWords() {
   482â†’    if (!animatedCaptionsState.captions) return [];
   483â†’
   484â†’    const words = [];
   485â†’    animatedCaptionsState.captions.forEach(caption => {
   486â†’        if (caption.words) {
   487â†’            caption.words.forEach(w => words.push(w));
   488â†’        }
   489â†’    });
   490â†’    return words;
   491â†’}
