   140â†’function addSpeaker() {
   141â†’    if (speakerConfig.length >= 4) {
   142â†’        setStatus('Maximum 4 speakers supported');
   143â†’        return;
   144â†’    }
   145â†’
   146â†’    const newIndex = speakerConfig.length;
   147â†’    const defaultName = `Speaker ${newIndex + 1}`;
   148â†’
   149â†’    speakerConfig.push({
   150â†’        index: newIndex,
   151â†’        name: defaultName,
   152â†’        videoTrack: newIndex
   153â†’    });
   154â†’
   155â†’    // Add DOM element
   156â†’    // SECURITY: Escape dynamic content to prevent XSS
   157â†’    const speakerItem = document.createElement('div');
   158â†’    speakerItem.className = `speaker-item speaker-${newIndex}`;
   159â†’    speakerItem.dataset.speakerIndex = newIndex;
   160â†’    speakerItem.innerHTML = `
   161â†’        <input type="text" class="speaker-name" value="${escapeHtml(defaultName)}" placeholder="Name" aria-label="Speaker ${newIndex + 1} name">
   162â†’        <select class="speaker-track" aria-label="Speaker ${newIndex + 1} video track">
   163â†’            <option value="0" ${newIndex === 0 ? 'selected' : ''}>V1</option>
   164â†’            <option value="1" ${newIndex === 1 ? 'selected' : ''}>V2</option>
   165â†’            <option value="2" ${newIndex === 2 ? 'selected' : ''}>V3</option>
   166â†’            <option value="3" ${newIndex === 3 ? 'selected' : ''}>V4</option>
   167â†’        </select>
   168â†’        <button class="speaker-remove-btn" title="Remove speaker" aria-label="Remove speaker ${newIndex + 1}">x</button>
   169â†’    `;
   170â†’
   171â†’    multitrackUI.speakerList.appendChild(speakerItem);
   172â†’
   173â†’    // Update remove button visibility
   174â†’    updateRemoveButtonVisibility();
   175â†’}
   176â†’
   177â†’function removeSpeaker(index) {
   178â†’    if (speakerConfig.length <= 2) {
   179â†’        setStatus('Minimum 2 speakers required');
   180â†’        return;
   181â†’    }
   182â†’
   183â†’    speakerConfig.splice(index, 1);
   184â†’
   185â†’    // Remove from DOM
   186â†’    const item = multitrackUI.speakerList.querySelector(`[data-speaker-index="${index}"]`);
   187â†’    if (item) {
   188â†’        item.remove();
   189â†’    }
   190â†’
   191â†’    // Re-index remaining speakers
   192â†’    reindexSpeakers();
   193â†’
   194â†’    // Update remove button visibility
   195â†’    updateRemoveButtonVisibility();
