   297â†’async function getAudioPath() {
   298â†’    try {
   299â†’        // Try to get audio path from active sequence
   300â†’        const seqInfo = await jsx.call('getActiveSequence');
   301â†’        if (seqInfo && seqInfo.audioPath) {
   302â†’            return seqInfo.audioPath;
   303â†’        }
   304â†’
   305â†’        // Fallback: export sequence audio for analysis
   306â†’        const exportResult = await jsx.call('exportSequenceAudioForAnalysis');
   307â†’        if (exportResult && exportResult.path) {
   308â†’            return exportResult.path;
   309â†’        }
   310â†’
   311â†’        // Last resort: get first clip audio path
   312â†’        const clipPath = await jsx.call('getFirstClipAudioPath');
   313â†’        if (clipPath) {
   314â†’            return clipPath;
   315â†’        }
   316â†’
   317â†’        throw new Error('Could not determine audio path');
   318â†’    } catch (e) {
   319â†’        console.error('[SPLICE Multitrack] Failed to get audio path:', e);
   320â†’        throw e;
   321â†’    }
   322â†’}
