   327â†’async function analyzeMultitrack() {
   328â†’    if (isMultitrackOperationInProgress) return;
   329â†’
   330â†’    // Check online status
   331â†’    if (typeof isOnline === 'function' && !isOnline()) {
   332â†’        setStatus('Offline - Check your connection');
   333â†’        return;
   334â†’    }
   335â†’
   336â†’    isMultitrackOperationInProgress = true;
   337â†’    if (multitrackUI.analyzeBtn) {
   338â†’        multitrackUI.analyzeBtn.disabled = true;
   339â†’        multitrackUI.analyzeBtn.textContent = 'Analyzing...';
   340â†’    }
   341â†’
   342â†’    try {
   343â†’        setStatus('Exporting audio tracks...');
   344â†’
   345â†’        // Get audio path
   346â†’        const audioPath = await getAudioPath();
   347â†’        if (!audioPath) {
   348â†’            throw new Error('Audio export path not initialized');
   349â†’        }
   350â†’
   351â†’        // For now, we use the main audio path
   352â†’        // In a full implementation, we'd export separate tracks per speaker
   353â†’        const audioPaths = [audioPath];
   354â†’
   355â†’        // Add additional tracks if multi-track sequence
   356â†’        if (speakerConfig.length > 1) {
   357â†’            for (let i = 1; i < speakerConfig.length; i++) {
   358â†’                audioPaths.push(audioPath);
   359â†’            }
   360â†’        }
   361â†’
   362â†’        setStatus('Analyzing speakers...');
   363â†’
   364â†’        const settings = getMultitrackSettings();
   365â†’        const apiUrl = getBackendUrl();
   366â†’
   367â†’        const response = await fetchWithTimeout(`${apiUrl}/multitrack`, {
   368â†’            method: 'POST',
   369â†’            headers: getAuthHeaders(),
   370â†’            body: JSON.stringify({
   371â†’                audioPaths,
   372â†’                speakerNames: settings.speakerNames,
   373â†’                videoTrackMapping: settings.videoTrackMapping,
   374â†’                wideShotEnabled: settings.wideShotEnabled,
   375â†’                wideShotPercentage: settings.wideShotPercentage,
   376â†’                wideShotTracks: settings.wideShotTracks,
   377â†’                minShotDuration: settings.minShotDuration,
   378â†’                switchingFrequency: settings.switchingFrequency
   379â†’            })
   380â†’        }, 120000);
   381â†’
   382â†’        if (!response.ok) {
   383â†’            const errorMsg = await parseErrorResponse(response);
   384â†’            throw new Error(errorMsg);
   385â†’        }
   386â†’
   387â†’        const data = await response.json();
   388â†’        analysisResults = data;
   389â†’
   390â†’        // Display results
   391â†’        displayMultitrackResults(data);
   392â†’
   393â†’        // Enable apply button
   394â†’        if (multitrackUI.applyBtn) {
   395â†’            multitrackUI.applyBtn.disabled = false;
   396â†’        }
   397â†’
   398â†’        setStatus(`Analysis complete - ${data.decisions?.length || 0} switching decisions`);
   399â†’
   400â†’    } catch (err) {
   401â†’        console.error('[SPLICE Multitrack] Analysis error:', err);
   402â†’        setStatus('Analysis failed: ' + err.message, true);
   403â†’    } finally {
   404â†’        isMultitrackOperationInProgress = false;
   405â†’        if (multitrackUI.analyzeBtn) {
   406â†’            multitrackUI.analyzeBtn.disabled = false;
   407â†’            multitrackUI.analyzeBtn.textContent = 'Analyze';
   408â†’        }
   409â†’    }
   410â†’}
