    13â†’
    14â†’const CUSTOM_PRESETS_KEY = 'spliceCustomPresets';
    15â†’
    16â†’/**
    17â†’ * Default structure for custom presets storage
    18â†’ */
    19â†’const DEFAULT_CUSTOM_PRESETS = {
    20â†’  version: 1,
    21â†’  presets: {},
    22â†’  order: []
    23â†’};
    24â†’
    25â†’/**
    26â†’ * Load custom presets from localStorage
