    35â†’      if (parsed && typeof parsed === 'object' && parsed.version && parsed.presets) {
    36â†’        // Return deep copy to avoid mutation issues
    37â†’        return {
    38â†’          version: parsed.version || 1,
    39â†’          presets: { ...parsed.presets },
