    46â†’  }
    47â†’  // Return fresh objects to avoid mutation of defaults
    48â†’  return {
    49â†’    version: 1,
    50â†’    presets: {},
    51â†’    order: []
    52â†’  };
    53â†’}
    54â†’
    55â†’/**
    56â†’ * Save custom presets to localStorage
    57â†’ * @param {Object} data - Custom presets data structure
    58â†’ * @returns {boolean} Success status
    59â†’ */
    60â†’function saveCustomPresets(data) {
    61â†’  try {
    62â†’    if (!data || typeof data !== 'object') {
    63â†’      console.warn('[SPLICE] Invalid custom presets data');
    64â†’      return false;
    65â†’    }
    66â†’    // Ensure required fields
    67â†’    const toSave = {
    68â†’      version: data.version || DEFAULT_CUSTOM_PRESETS.version,
    69â†’      presets: data.presets || {},
    70â†’      order: data.order || []
    71â†’    };
    72â†’    localStorage.setItem(CUSTOM_PRESETS_KEY, JSON.stringify(toSave));
    73â†’    return true;
    74â†’  } catch (e) {
    75â†’    console.warn('[SPLICE] Could not save custom presets:', e);
    76â†’    return false;
    77â†’  }
    78â†’}
    79â†’
    80â†’/**
    81â†’ * Generate a slugified ID from a preset name
    82â†’ * @param {string} name - Preset name
    83â†’ * @returns {string} Slugified ID
    84â†’ */
    85â†’function slugifyPresetName(name) {
    86â†’  return name
    87â†’    .toLowerCase()
    88â†’    .trim()
    89â†’    .replace(/[^a-z0-9]+/g, '-')
    90â†’    .replace(/^-+|-+$/g, '')
    91â†’    .substring(0, 50) || 'preset';
    92â†’}
    93â†’
    94â†’/**
    95â†’ * Generate a unique ID for a custom preset
    96â†’ * @param {string} baseName - Base name to slugify
    97â†’ * @param {Object} existingPresets - Current presets object
    98â†’ * @returns {string} Unique ID
    99â†’ */
   100â†’function generateUniquePresetId(baseName, existingPresets) {
   101â†’  const baseId = slugifyPresetName(baseName);
   102â†’  let id = baseId;
   103â†’  let counter = 1;
   104â†’
   105â†’  while (existingPresets[id]) {
   106â†’    id = `${baseId}-${counter}`;
   107â†’    counter++;
   108â†’  }
   109â†’
   110â†’  return id;
   111â†’}
   112â†’
   113â†’/**
   114â†’ * Create a new custom preset
   115â†’ * @param {Object} preset - Preset data
   116â†’ * @param {string} preset.name - Preset name (required)
   117â†’ * @param {string} [preset.description] - Preset description
   118â†’ * @param {string} [preset.icon] - Icon name (default: 'settings')
   119â†’ * @param {Object} [preset.settings] - Preset settings (uses current if not provided)
   120â†’ * @returns {{success: boolean, id?: string, error?: string}}
   121â†’ */
   122â†’function createCustomPreset(preset) {
   123â†’  // Validate input
   124â†’  if (!preset || typeof preset !== 'object') {
   125â†’    return { success: false, error: 'Invalid preset data' };
   126â†’  }
   127â†’
   128â†’  if (!preset.name || typeof preset.name !== 'string' || !preset.name.trim()) {
   129â†’    return { success: false, error: 'Preset name is required' };
   130â†’  }
   131â†’
   132â†’  const name = preset.name.trim();
   133â†’
   134â†’  // Load current data
   135â†’  const data = loadCustomPresets();
   136â†’
   137â†’  // Generate unique ID
   138â†’  const id = generateUniquePresetId(name, data.presets);
   139â†’
   140â†’  // Get settings from preset or current settings
   141â†’  const settings = preset.settings && typeof preset.settings === 'object'
   142â†’    ? { ...preset.settings }
   143â†’    : getCurrentPresetSettings();
   144â†’
   145â†’  // Validate settings
   146â†’  if (!settings || typeof settings !== 'object') {
   147â†’    return { success: false, error: 'Invalid settings' };
   148â†’  }
   149â†’
   150â†’  // Create the preset object
   151â†’  const newPreset = {
   152â†’    id,
   153â†’    name,
   154â†’    description: preset.description || '',
   155â†’    icon: preset.icon || 'settings',
   156â†’    createdAt: new Date().toISOString(),
   157â†’    settings
   158â†’  };
   159â†’
   160â†’  // Add to presets and order
   161â†’  data.presets[id] = newPreset;
   162â†’  data.order.push(id);
   163â†’
   164â†’  // Save
   165â†’  const saved = saveCustomPresets(data);
   166â†’  if (!saved) {
   167â†’    return { success: false, error: 'Failed to save preset' };
   168â†’  }
   169â†’
   170â†’  console.log(`[SPLICE] Created custom preset: ${name} (${id})`);
   171â†’
   172â†’  // Refresh preset dropdown if available
   173â†’  if (typeof populatePresetDropdown === 'function') {
   174â†’    populatePresetDropdown(id);
   175â†’  }
   176â†’
   177â†’  return { success: true, id };
   178â†’}
   179â†’
   180â†’/**
   181â†’ * Get current settings suitable for a preset
   182â†’ * @returns {Object} Current preset-compatible settings
   183â†’ */
   184â†’function getCurrentPresetSettings() {
   185â†’  const current = typeof getSettings === 'function' ? getSettings() : {};
   186â†’  return {
   187â†’    sensitivity: current.sensitivity || 50,
   188â†’    threshold: current.threshold || -32,
   189â†’    minSilenceLength: current.minSilenceLength || 0.5,
   190â†’    paddingStart: current.paddingStart || 0.1,
   191â†’    paddingEnd: current.paddingEnd || 0.1,
   192â†’    autoMarkBest: current.autoMarkBest !== false,
   193â†’    enableTakesDetection: current.enableTakesDetection !== false
   194â†’  };
   195â†’}
   196â†’
   197â†’/**
   198â†’ * Update an existing custom preset
   199â†’ * @param {string} id - Preset ID to update
   200â†’ * @param {Object} updates - Fields to update
   201â†’ * @param {string} [updates.name] - New name
   202â†’ * @param {string} [updates.description] - New description
   203â†’ * @param {string} [updates.icon] - New icon
   204â†’ * @param {Object} [updates.settings] - New settings (merged with existing)
   205â†’ * @returns {{success: boolean, error?: string}}
   206â†’ */
   207â†’function updateCustomPreset(id, updates) {
   208â†’  // Validate input
   209â†’  if (!id || typeof id !== 'string') {
   210â†’    return { success: false, error: 'Preset ID is required' };
   211â†’  }
   212â†’
   213â†’  if (!updates || typeof updates !== 'object') {
   214â†’    return { success: false, error: 'Updates must be an object' };
   215â†’  }
   216â†’
   217â†’  // Load current data
   218â†’  const data = loadCustomPresets();
   219â†’
   220â†’  // Check if preset exists
   221â†’  if (!data.presets[id]) {
   222â†’    return { success: false, error: 'Preset not found' };
   223â†’  }
   224â†’
   225â†’  const existing = data.presets[id];
   226â†’
   227â†’  // Apply updates (preserve fields not being updated)
   228â†’  const updated = {
   229â†’    ...existing,
   230â†’    updatedAt: new Date().toISOString()
   231â†’  };
   232â†’
   233â†’  // Update name (with validation)
   234â†’  if (updates.name !== undefined) {
   235â†’    if (typeof updates.name !== 'string' || !updates.name.trim()) {
   236â†’      return { success: false, error: 'Name must be a non-empty string' };
   237â†’    }
   238â†’    updated.name = updates.name.trim();
   239â†’  }
   240â†’
   241â†’  // Update description
   242â†’  if (updates.description !== undefined) {
   243â†’    updated.description = String(updates.description);
   244â†’  }
   245â†’
   246â†’  // Update icon
   247â†’  if (updates.icon !== undefined) {
   248â†’    updated.icon = String(updates.icon);
   249â†’  }
   250â†’
   251â†’  // Update settings (merge with existing)
   252â†’  if (updates.settings !== undefined) {
   253â†’    if (typeof updates.settings !== 'object') {
   254â†’      return { success: false, error: 'Settings must be an object' };
   255â†’    }
   256â†’    updated.settings = {
   257â†’      ...existing.settings,
   258â†’      ...updates.settings
   259â†’    };
   260â†’  }
   261â†’
   262â†’  // Save updated preset
   263â†’  data.presets[id] = updated;
   264â†’
   265â†’  const saved = saveCustomPresets(data);
   266â†’  if (!saved) {
   267â†’    return { success: false, error: 'Failed to save preset' };
   268â†’  }
   269â†’
   270â†’  console.log(`[SPLICE] Updated custom preset: ${updated.name} (${id})`);
   271â†’  return { success: true };
   272â†’}
   273â†’
   274â†’/**
   275â†’ * Get a custom preset by ID
   276â†’ * @param {string} id - Preset ID
   277â†’ * @returns {Object|null} Preset object or null if not found
   278â†’ */
   279â†’function getCustomPreset(id) {
   280â†’  if (!id || typeof id !== 'string') {
   281â†’    return null;
   282â†’  }
   283â†’
   284â†’  const data = loadCustomPresets();
   285â†’  return data.presets[id] || null;
   286â†’}
   287â†’
   288â†’/**
   289â†’ * Duplicate a custom preset
   290â†’ * @param {string} id - Preset ID to duplicate
   291â†’ * @returns {{success: boolean, id?: string, error?: string}}
   292â†’ */
   293â†’function duplicatePreset(id) {
   294â†’  // Validate input
   295â†’  if (!id || typeof id !== 'string') {
   296â†’    return { success: false, error: 'Preset ID is required' };
   297â†’  }
   298â†’
   299â†’  // Get the original preset
   300â†’  const original = getPresetById(id);
   301â†’  if (!original) {
   302â†’    return { success: false, error: 'Preset not found' };
   303â†’  }
   304â†’
   305â†’  // Generate new name with copy suffix
   306â†’  let copyName = `${original.name} (Copy)`;
   307â†’
   308â†’  // Check if copy name already exists and increment if needed
   309â†’  const data = loadCustomPresets();
   310â†’  let copyNumber = 1;
   311â†’  let testId = slugifyPresetName(copyName);
   312â†’  while (data.presets[testId]) {
   313â†’    copyNumber++;
   314â†’    copyName = `${original.name} (Copy ${copyNumber})`;
   315â†’    testId = slugifyPresetName(copyName);
   316â†’  }
   317â†’
   318â†’  // Create the duplicate
   319â†’  return createCustomPreset({
   320â†’    name: copyName,
   321â†’    description: original.description || '',
   322â†’    icon: original.icon || 'settings',
   323â†’    settings: original.settings ? { ...original.settings } : getCurrentPresetSettings()
   324â†’  });
   325â†’}
   326â†’
   327â†’/**
   328â†’ * Export all custom presets to JSON string
   329â†’ * @returns {{success: boolean, data?: string, count?: number, error?: string}}
   330â†’ */
   331â†’function exportPresets() {
   332â†’  try {
   333â†’    const data = loadCustomPresets();
   334â†’    const customPresets = data.order.map(id => data.presets[id]).filter(Boolean);
   335â†’
   336â†’    if (customPresets.length === 0) {
   337â†’      return { success: false, error: 'No custom presets to export' };
   338â†’    }
   339â†’
   340â†’    const exportData = {
   341â†’      version: 1,
   342â†’      exportedAt: new Date().toISOString(),
   343â†’      presets: customPresets
   344â†’    };
   345â†’
   346â†’    return {
   347â†’      success: true,
   348â†’      data: JSON.stringify(exportData, null, 2),
   349â†’      count: customPresets.length
   350â†’    };
   351â†’  } catch (e) {
   352â†’    return { success: false, error: 'Failed to export presets: ' + e.message };
   353â†’  }
   354â†’}
   355â†’
   356â†’/**
   357â†’ * Import presets from JSON string
   358â†’ * @param {string} jsonString - JSON string containing presets
   359â†’ * @param {boolean} merge - If true, merge with existing; if false, skip duplicates
   360â†’ * @returns {{success: boolean, imported?: number, skipped?: number, error?: string}}
   361â†’ */
   362â†’function importPresets(jsonString, merge = true) {
   363â†’  try {
   364â†’    // Parse JSON
   365â†’    const imported = JSON.parse(jsonString);
   366â†’
   367â†’    // Validate structure
   368â†’    if (!imported || !imported.presets || !Array.isArray(imported.presets)) {
   369â†’      return { success: false, error: 'Invalid preset file format' };
   370â†’    }
   371â†’
   372â†’    let importedCount = 0;
   373â†’    let skippedCount = 0;
   374â†’
   375â†’    for (const preset of imported.presets) {
   376â†’      // Validate each preset has required fields
   377â†’      if (!preset.name || typeof preset.name !== 'string') {
   378â†’        skippedCount++;
   379â†’        continue;
   380â†’      }
   381â†’
   382â†’      // Check if a preset with this name already exists
   383â†’      const existingId = slugifyPresetName(preset.name);
   384â†’      const existing = getCustomPreset(existingId);
   385â†’
   386â†’      if (existing && !merge) {
   387â†’        // Skip existing if not merging
   388â†’        skippedCount++;
   389â†’        continue;
   390â†’      }
   391â†’
   392â†’      // If existing and merge mode, create with unique name
   393â†’      if (existing && merge) {
   394â†’        let newName = `${preset.name} (Imported)`;
   395â†’        let counter = 1;
   396â†’        while (getCustomPreset(slugifyPresetName(newName))) {
   397â†’          counter++;
   398â†’          newName = `${preset.name} (Imported ${counter})`;
   399â†’        }
   400â†’        preset.name = newName;
   401â†’      }
   402â†’
   403â†’      // Create the preset
   404â†’      const result = createCustomPreset({
   405â†’        name: preset.name,
   406â†’        description: preset.description || '',
   407â†’        icon: preset.icon || 'settings',
   408â†’        settings: preset.settings || getCurrentPresetSettings()
   409â†’      });
   410â†’
   411â†’      if (result.success) {
   412â†’        importedCount++;
   413â†’      } else {
   414â†’        skippedCount++;
   415â†’      }
   416â†’    }
   417â†’
   418â†’    if (importedCount === 0 && skippedCount > 0) {
   419â†’      return { success: false, error: 'All presets already exist or were invalid', skipped: skippedCount };
   420â†’    }
   421â†’
   422â†’    return {
   423â†’      success: true,
   424â†’      imported: importedCount,
   425â†’      skipped: skippedCount
   426â†’    };
   427â†’  } catch (e) {
   428â†’    if (e instanceof SyntaxError) {
   429â†’      return { success: false, error: 'Invalid JSON format' };
   430â†’    }
   431â†’    return { success: false, error: 'Failed to import presets: ' + e.message };
   432â†’  }
   433â†’}
   434â†’
   435â†’/**
   436â†’ * Delete a custom preset
   437â†’ * @param {string} id - Preset ID to delete
   438â†’ * @returns {{success: boolean, error?: string}}
   439â†’ */
   440â†’function deleteCustomPreset(id) {
   441â†’  // Validate input
   442â†’  if (!id || typeof id !== 'string') {
   443â†’    return { success: false, error: 'Preset ID is required' };
   444â†’  }
   445â†’
   446â†’  // Load current data
   447â†’  const data = loadCustomPresets();
   448â†’
   449â†’  // Check if preset exists
   450â†’  if (!data.presets[id]) {
   451â†’    return { success: false, error: 'Preset not found' };
   452â†’  }
   453â†’
   454â†’  const presetName = data.presets[id].name;
   455â†’
   456â†’  // Remove from presets object
   457â†’  delete data.presets[id];
   458â†’
   459â†’  // Remove from order array
   460â†’  const orderIndex = data.order.indexOf(id);
   461â†’  if (orderIndex !== -1) {
   462â†’    data.order.splice(orderIndex, 1);
   463â†’  }
   464â†’
   465â†’  // Save
   466â†’  const saved = saveCustomPresets(data);
   467â†’  if (!saved) {
   468â†’    return { success: false, error: 'Failed to save after deletion' };
   469â†’  }
   470â†’
   471â†’  console.log(`[SPLICE] Deleted custom preset: ${presetName} (${id})`);
   472â†’
   473â†’  // Refresh preset dropdown if available
   474â†’  if (typeof populatePresetDropdown === 'function') {
   475â†’    // Reset to custom if deleted preset was selected
   476â†’    const settings = getSettings();
   477â†’    const selectedPreset = settings.activePreset === id ? 'custom' : settings.activePreset;
