   498â†’/**
   499â†’ * Get all presets (built-in + custom merged)
   500â†’ * Built-in presets appear first, then custom presets in their creation order.
   501â†’ * Each preset has isBuiltIn flag added.
   502â†’ *
   503â†’ * @returns {Array<Object>} Array of preset objects with metadata
   504â†’ */
   505â†’function getAllPresets() {
   506â†’  // Get built-in presets
   507â†’  const builtInPresets = BUILT_IN_PRESET_IDS.map(id => {
   508â†’    const preset = PRESETS[id];
   509â†’    return {
   510â†’      id,
   511â†’      name: preset.name,
   512â†’      description: preset.description,
   513â†’      icon: preset.icon,
   514â†’      settings: preset.settings,
   515â†’      isBuiltIn: true
   516â†’    };
   517â†’  });
   518â†’
   519â†’  // Get custom presets
   520â†’  const customData = loadCustomPresets();
   521â†’  const customPresets = customData.order.map(id => {
   522â†’    const preset = customData.presets[id];
   523â†’    if (!preset) return null;
   524â†’    return {
   525â†’      ...preset,
   526â†’      isBuiltIn: false
   527â†’    };
   528â†’  }).filter(Boolean);
   529â†’
   530â†’  // Merge: built-in first, then custom
   531â†’  return [...builtInPresets, ...customPresets];
