  1121â†’/**
  1122â†’ * Media folder entry (cached after selection)
  1123â†’ * @type {Object|null}
  1124â†’ */
  1125â†’let mediaFolderEntry = null;
  1126â†’
  1127â†’/**
  1128â†’ * Prompt user to select a media folder and create a persistent token.
  1129â†’ * This allows silent file access without repeated permission prompts.
  1130â†’ *
  1131â†’ * @returns {Promise<{success: boolean, path?: string, error?: string}>}
  1132â†’ */
  1133â†’async function setupMediaFolder() {
  1134â†’  try {
  1135â†’    // Open folder picker dialog
  1136â†’    const folder = await uxpFs.getFolder();
  1137â†’
  1138â†’    if (!folder) {
  1139â†’      return { success: false, error: 'No folder selected' };
  1140â†’    }
  1141â†’
  1142â†’    // Create a persistent token for this folder
  1143â†’    const token = await uxpFs.createPersistentToken(folder);
  1144â†’
  1145â†’    if (!token) {
  1146â†’      return { success: false, error: 'Failed to create persistent token' };
  1147â†’    }
  1148â†’
  1149â†’    // Get the folder path for display
  1150â†’    const folderPath = folder.nativePath || folder.name;
  1151â†’
  1152â†’    // Save to settings
  1153â†’    saveSettings({
  1154â†’      mediaFolderToken: token,
  1155â†’      mediaFolderPath: folderPath
  1156â†’    });
  1157â†’
  1158â†’    // Cache the folder entry
  1159â†’    mediaFolderEntry = folder;
  1160â†’
  1161â†’    console.log(`[SPLICE] Media folder set: ${folderPath}`);
  1162â†’
  1163â†’    return {
  1164â†’      success: true,
  1165â†’      path: folderPath,
  1166â†’      token
  1167â†’    };
  1168â†’  } catch (err) {
  1169â†’    console.error('[SPLICE] Error setting up media folder:', err);
  1170â†’    return { success: false, error: err.message };
  1171â†’  }
  1172â†’}
  1173â†’
  1174â†’/**
  1175â†’ * Get the media folder entry using the stored persistent token.
  1176â†’ * Falls back to prompting if token is invalid.
  1177â†’ *
  1178â†’ * @returns {Promise<Object|null>} The folder entry or null
  1179â†’ */
  1180â†’async function getMediaFolder() {
  1181â†’  // Return cached entry if available
  1182â†’  if (mediaFolderEntry) {
  1183â†’    return mediaFolderEntry;
  1184â†’  }
  1185â†’
  1186â†’  const settings = getSettings();
  1187â†’
  1188â†’  // Try to restore from persistent token
  1189â†’  if (settings.mediaFolderToken) {
  1190â†’    try {
  1191â†’      const folder = await uxpFs.getEntryForPersistentToken(settings.mediaFolderToken);
  1192â†’      if (folder) {
  1193â†’        mediaFolderEntry = folder;
  1194â†’        console.log(`[SPLICE] Restored media folder from token: ${settings.mediaFolderPath}`);
  1195â†’        return folder;
  1196â†’      }
  1197â†’    } catch (err) {
  1198â†’      console.warn('[SPLICE] Could not restore media folder from token:', err.message);
  1199â†’      // Clear invalid token
  1200â†’      saveSettings({ mediaFolderToken: null, mediaFolderPath: null });
  1201â†’    }
  1202â†’  }
  1203â†’
  1204â†’  return null;
  1205â†’}
