  1237â†’/**
  1238â†’ * Read a file from the media folder
  1239â†’ * @param {string} filename - Name of the file to read
  1240â†’ * @returns {Promise<ArrayBuffer|null>} File contents or null
  1241â†’ */
  1242â†’async function readMediaFile(filename) {
  1243â†’  const folder = await getMediaFolder();
  1244â†’  if (!folder) {
  1245â†’    console.warn('[SPLICE] No media folder configured');
  1246â†’    return null;
  1247â†’  }
  1248â†’
  1249â†’  try {
  1250â†’    const file = await folder.getEntry(filename);
  1251â†’    if (!file) {
