  1268â†’/**
  1269â†’ * Validate license key format: SPLICE-XXXX-XXXX-XXXX
  1270â†’ * Character set excludes confusing chars: 0/O, 1/I/L
  1271â†’ * @param {string} key - License key to validate
  1272â†’ * @returns {boolean} True if format is valid
  1273â†’ */
  1274â†’function isValidLicenseKeyFormat(key) {
  1275â†’  if (!key || typeof key !== 'string') return false;
  1276â†’  const normalized = key.toUpperCase().trim();
  1277â†’  const pattern = /^SPLICE-[ABCDEFGHJKMNPQRSTUVWXYZ23456789]{4}-[ABCDEFGHJKMNPQRSTUVWXYZ23456789]{4}-[ABCDEFGHJKMNPQRSTUVWXYZ23456789]{4}$/;
  1278â†’  return pattern.test(normalized);
  1279â†’}
