  1569â†’/**
  1570â†’ * Parse comma-separated word list into array
  1571â†’ * @param {string} text - Comma or newline separated words
  1572â†’ * @returns {Array<string>} Array of trimmed words
  1573â†’ */
  1574â†’function parseWordList(text) {
  1575â†’  if (!text || typeof text !== 'string') return [];
  1576â†’  return text
  1577â†’    .split(/[,\n]/)
  1578â†’    .map(word => word.trim().toLowerCase())
  1579â†’    .filter(word => word.length > 0);
  1580â†’}
  1581â†’
  1582â†’/**
  1583â†’ * Format array of words as comma-separated string
  1584â†’ * @param {Array<string>} words - Array of words
