    34â†’// ============================================================================
    35â†’// JSX BRIDGE - Communicate with Premiere Pro
    36â†’// ============================================================================
    37â†’const jsx = {
    38â†’    cs: null,
    39â†’    _initPromise: null,
    40â†’    _initialized: false,
    41â†’
    42â†’    /**
    43â†’     * Initialize the CSInterface
    44â†’     * Returns a promise that resolves when ready
    45â†’     */
    46â†’    init: function() {
    47â†’        if (this._initPromise) return this._initPromise;
    48â†’
    49â†’        this._initPromise = new Promise((resolve) => {
    50â†’            if (typeof CSInterface !== 'undefined') {
    51â†’                this.cs = new CSInterface();
    52â†’                this._initialized = true;
    53â†’                console.log('[JSX] CSInterface initialized successfully');
    54â†’            } else {
    55â†’                console.warn('[JSX] CSInterface not available');
    56â†’            }
    57â†’            resolve(this._initialized);
    58â†’        });
    59â†’
    60â†’        return this._initPromise;
    61â†’    },
    62â†’
    63â†’    /**
    64â†’     * Ensure JSX is initialized before making calls
    65â†’     */
    66â†’    ensureInitialized: async function() {
    67â†’        if (!this._initialized) {
    68â†’            await this.init();
    69â†’        }
    70â†’        if (!this.cs) {
    71â†’            throw new Error('CSInterface not initialized. Make sure you are running in CEP environment.');
    72â†’        }
    73â†’    },
    74â†’
    75â†’    /**
    76â†’     * Execute ExtendScript in Premiere Pro
    77â†’     * @param {string} script - ExtendScript to execute
    78â†’     * @returns {Promise<any>} - Parsed result
    79â†’     */
    80â†’    evalScript: async function(script) {
    81â†’        await this.ensureInitialized();
    82â†’
    83â†’        return new Promise((resolve, reject) => {
    84â†’            this.cs.evalScript(script, (result) => {
    85â†’                if (result === 'undefined' || result === undefined) {
    86â†’                    resolve(null);
    87â†’                    return;
    88â†’                }
    89â†’
    90â†’                // Check for EvalScript error or ExtendScript runtime errors
    91â†’                if (typeof result === 'string' && (
    92â†’                    result.indexOf('EvalScript error') !== -1 ||
    93â†’                    result.indexOf('Error:') !== -1 ||
    94â†’                    result.indexOf('TypeError:') !== -1 ||
    95â†’                    result.indexOf('ReferenceError:') !== -1
    96â†’                )) {
    97â†’                    reject(new Error(result));
    98â†’                    return;
    99â†’                }
   100â†’
   101â†’                // Try to parse JSON
   102â†’                try {
   103â†’                    const parsed = JSON.parse(result);
   104â†’                    if (parsed.error) {
   105â†’                        reject(new Error(parsed.error));
   106â†’                    } else {
   107â†’                        resolve(parsed);
   108â†’                    }
   109â†’                } catch (e) {
   110â†’                    // Return as-is if not JSON
   111â†’                    resolve(result);
   112â†’                }
   113â†’            });
   114â†’        });
   115â†’    },
   116â†’
   117â†’    /**
   118â†’     * Call a JSX function with arguments
   119â†’     * @param {string} funcName - Function name
   120â†’     * @param {...any} args - Arguments to pass
   121â†’     * @returns {Promise<any>}
   122â†’     */
   123â†’    call: async function(funcName, ...args) {
   124â†’        await this.ensureInitialized();
   125â†’
   126â†’        const escapedArgs = args.map(arg => {
   127â†’            if (arg === null || arg === undefined) return 'null';
   128â†’            if (typeof arg === 'string') {
   129â†’                // Escape backslashes first, then other special chars
   130â†’                return `'${arg
   131â†’                    .replace(/\\/g, '\\\\')
   132â†’                    .replace(/'/g, "\\'")
   133â†’                    .replace(/\r/g, '\\r')
   134â†’                    .replace(/\n/g, '\\n')
   135â†’                    .replace(/\t/g, '\\t')}'`;
   136â†’            }
   137â†’            if (typeof arg === 'object') {
   138â†’                return `'${JSON.stringify(arg)
   139â†’                    .replace(/\\/g, '\\\\')
   140â†’                    .replace(/'/g, "\\'")}'`;
   141â†’            }
   142â†’            return String(arg);
   143â†’        });
   144â†’
   145â†’        const script = `${funcName}(${escapedArgs.join(', ')})`;
   146â†’        return this.evalScript(script);
   147â†’    }
   148â†’};
