   153â†’
   154â†’/**
   155â†’ * Fetch with timeout support
   156â†’ * @param {string} url - URL to fetch
   157â†’ * @param {object} options - Fetch options
   158â†’ * @param {number} timeout - Timeout in ms
   159â†’ */
   160â†’async function fetchWithTimeout(url, options = {}, timeout = SPLICE_CONFIG.FETCH_TIMEOUT) {
   161â†’    const controller = new AbortController();
   162â†’    const timeoutId = setTimeout(() => controller.abort(), timeout);
   163â†’
   164â†’    try {
   165â†’        const response = await fetch(url, {
   166â†’            ...options,
   167â†’            signal: controller.signal
   168â†’        });
   169â†’        clearTimeout(timeoutId);
   170â†’        return response;
   171â†’    } catch (error) {
   172â†’        clearTimeout(timeoutId);
   173â†’        if (error.name === 'AbortError') {
   174â†’            throw new Error(`Request timeout after ${timeout}ms`);
   175â†’        }
   176â†’        throw error;
   177â†’    }
   178â†’}
   179â†’
   180â†’/**
   181â†’ * Parse error response from backend
   182â†’ * @param {Response} response - Fetch response
   183â†’ */
   184â†’async function parseErrorResponse(response) {
   185â†’    try {
   186â†’        const data = await response.json();
   187â†’        return data.error || data.message || `HTTP ${response.status}`;
   188â†’    } catch {
   189â†’        return `HTTP ${response.status}: ${response.statusText}`;
   190â†’    }
   191â†’}
