   193â†’/**
   194â†’ * Get auth headers for API requests
   195â†’ */
   196â†’function getAuthHeaders() {
   197â†’    const headers = { 'Content-Type': 'application/json' };
   198â†’    const settings = getSettings();
   199â†’
   200â†’    if (settings.customerId) {
   201â†’        headers['x-stripe-customer-id'] = settings.customerId;
   202â†’    }
   203â†’    if (settings.licenseKey) {
   204â†’        headers['x-license-key'] = settings.licenseKey;
   205â†’    }
   206â†’
   207â†’    return headers;
   208â†’}
