   239â†’/**
   240â†’ * Get settings from localStorage
   241â†’ */
   242â†’function getSettings() {
   243â†’    try {
   244â†’        const stored = localStorage.getItem('spliceSettings');
   245â†’        if (stored) {
   246â†’            return { ...DEFAULT_SETTINGS, ...JSON.parse(stored) };
   247â†’        }
