   260â†’        localStorage.setItem('spliceSettings', JSON.stringify(merged));
   261â†’        return true;
   262â†’    } catch (e) {
   263â†’        console.warn('[Config] Error saving settings:', e);
   264â†’        return false;
   265â†’    }
