   280â†’
   281â†’let isOnlineState = true;
   282â†’let offlineCheckInterval = null;
   283â†’
   284â†’/**
   285â†’ * Check if online
   286â†’ */
   287â†’function isOnline() {
   288â†’    return isOnlineState && navigator.onLine;
   289â†’}
   290â†’
   291â†’/**
   292â†’ * Initialize offline detection
   293â†’ */
   294â†’function initOfflineDetection() {
   295â†’    // Browser events
   296â†’    window.addEventListener('online', () => {
   297â†’        isOnlineState = true;
   298â†’        updateOfflineUI(true);
   299â†’    });
   300â†’
   301â†’    window.addEventListener('offline', () => {
   302â†’        isOnlineState = false;
   303â†’        updateOfflineUI(false);
   304â†’    });
   305â†’
   306â†’    // Periodic check
   307â†’    offlineCheckInterval = setInterval(async () => {
   308â†’        try {
   309â†’            const response = await fetchWithTimeout(
   310â†’                `${getBackendUrl()}/health`,
   311â†’                { method: 'GET' },
   312â†’                5000
   313â†’            );
   314â†’            isOnlineState = response.ok;
   315â†’        } catch {
   316â†’            isOnlineState = false;
   317â†’        }
   318â†’        updateOfflineUI(isOnlineState);
   319â†’    }, 30000);
   320â†’}
