   322â†’/**
   323â†’ * Update UI based on online state
   324â†’ */
   325â†’function updateOfflineUI(online) {
   326â†’    const goBtn = document.getElementById('goBtn');
   327â†’    const status = document.getElementById('status');
   328â†’
   329â†’    if (goBtn) {
   330â†’        goBtn.disabled = !online;
   331â†’    }
   332â†’
   333â†’    if (status) {
   334â†’        if (!online) {
   335â†’            status.textContent = 'Offline - Check your connection';
   336â†’            status.style.color = '#dc3545';
   337â†’        } else {
   338â†’            // Reset status when back online
   339â†’            status.textContent = 'Ready';
   340â†’            status.style.color = '#888';
   341â†’        }
   342â†’    }
   343â†’}
