   349â†’/**
   350â†’ * SECURITY: Escape HTML to prevent XSS attacks
   351â†’ * Use this when inserting untrusted content into innerHTML
   352â†’ * @param {string} str - String to escape
   353â†’ * @returns {string} Escaped string safe for innerHTML
   354â†’ */
   355â†’function escapeHtml(str) {
   356â†’    if (str === null || str === undefined) return '';
   357â†’    if (typeof str !== 'string') str = String(str);
   358â†’
   359â†’    const escapeMap = {
   360â†’        '&': '&amp;',
   361â†’        '<': '&lt;',
   362â†’        '>': '&gt;',
   363â†’        '"': '&quot;',
   364â†’        "'": '&#x27;',
   365â†’        '/': '&#x2F;',
   366â†’        '`': '&#x60;'
   367â†’    };
   368â†’
   369â†’    return str.replace(/[&<>"'\/`]/g, char => escapeMap[char]);
   370â†’}
   371â†’
   372â†’/**
   373â†’ * SECURITY: Create safe HTML from template with escaped values
   374â†’ * @param {TemplateStringsArray} strings - Template strings
   375â†’ * @param {...any} values - Values to escape
   376â†’ * @returns {string} HTML with escaped values
   377â†’ */
   378â†’function safeHtml(strings, ...values) {
   379â†’    return strings.reduce((result, str, i) => {
   380â†’        const value = values[i - 1];
   381â†’        const escaped = escapeHtml(value);
   382â†’        return result + escaped + str;
   383â†’    });
   384â†’}
   385â†’
   386â†’/**
   387â†’ * Format time in MM:SS or HH:MM:SS
   388â†’ */
   389â†’function formatTime(seconds) {
   390â†’    if (isNaN(seconds) || seconds < 0) return '0:00';
   391â†’
   392â†’    const hours = Math.floor(seconds / 3600);
   393â†’    const minutes = Math.floor((seconds % 3600) / 60);
   394â†’    const secs = Math.floor(seconds % 60);
   395â†’
   396â†’    if (hours > 0) {
   397â†’        return `${hours}:${minutes.toString().padStart(2, '0')}:${secs.toString().padStart(2, '0')}`;
   398â†’    }
   399â†’    return `${minutes}:${secs.toString().padStart(2, '0')}`;
   400â†’}
