   405â†’function setStatus(message, isError = false) {
   406â†’    const status = document.getElementById('status');
   407â†’    if (status) {
   408â†’        status.textContent = message;
   409â†’        status.style.color = isError ? '#dc3545' : '#888';
   410â†’    }
   411â†’}
