   413â†’/**
   414â†’ * Debounce function calls
   415â†’ */
   416â†’function debounce(func, wait) {
   417â†’    let timeout;
   418â†’    return function executedFunction(...args) {
   419â†’        const later = () => {
   420â†’            clearTimeout(timeout);
   421â†’            func(...args);
   422â†’        };
   423â†’        clearTimeout(timeout);
   424â†’        timeout = setTimeout(later, wait);
   425â†’    };
   426â†’}
