   567â†’/**
   568â†’ * Apply a preset to current settings
   569â†’ * @param {string} presetName - Name of the preset to apply
   570â†’ * @returns {Object} The applied settings
   571â†’ */
   572â†’function applyPreset(presetName) {
   573â†’    const preset = PRESETS[presetName];
   574â†’
   575â†’    if (!preset) {
   576â†’        console.warn(`[SPLICE] Unknown preset: ${presetName}`);
   577â†’        return getSettings();
   578â†’    }
   579â†’
   580â†’    // Custom preset uses current settings
   581â†’    if (presetName === 'custom' || !preset.settings) {
   582â†’        saveSettings({ activePreset: 'custom' });
   583â†’        return getSettings();
   584â†’    }
   585â†’
   586â†’    // Apply preset settings
   587â†’    const newSettings = {
   588â†’        ...preset.settings,
   589â†’        activePreset: presetName
   590â†’    };
   591â†’
   592â†’    saveSettings(newSettings);
   593â†’    console.log(`[SPLICE] Applied preset: ${preset.name}`);
   594â†’
   595â†’    return getSettings();
   596â†’}
