  1681â†’function formatDuration(seconds) {
  1682â†’  if (!seconds) return '0:00';
  1683â†’  const mins = Math.floor(seconds / 60);
  1684â†’  const secs = Math.round(seconds % 60);
  1685â†’  return `${mins}:${secs.toString().padStart(2, '0')}`;
  1686â†’}
