   529â†’async function runDetection() {
   530â†’    if (isOperationInProgress) return;
   531â†’
   532â†’    // Check sequence
   533â†’    const hasSequence = await checkSequenceOpen();
   534â†’    if (!hasSequence) {
   535â†’        setStatus('Please open a sequence first', true);
   536â†’        return;
   537â†’    }
   538â†’
   539â†’    // Check online
   540â†’    if (!isOnline()) {
   541â†’        setStatus('Offline - Check your connection', true);
   542â†’        return;
   543â†’    }
   544â†’
   545â†’    isOperationInProgress = true;
   546â†’    ui.goBtn.disabled = true;
   547â†’    showProgress('Detecting silences...');
   548â†’
   549â†’    try {
   550â†’        const settings = getSettings();
   551â†’
   552â†’        // Get sequence info
   553â†’        const seqInfo = await jsx.call('getActiveSequence');
   554â†’        console.log('[SPLICE] Sequence info:', seqInfo);
   555â†’
   556â†’        // Store sequence info for later use
   557â†’        currentSequenceInfo = seqInfo;
   558â†’        window.spliceState.activeSequence = seqInfo;
   559â†’        if (seqInfo && seqInfo.videoPath) {
   560â†’            window.spliceState.currentVideoPath = seqInfo.videoPath;
   561â†’        }
   562â†’
   563â†’        // Export audio via JSX for silence detection
   564â†’        updateProgress(10, 'Exporting audio...');
   565â†’
   566â†’        let audioFilePath = null;
   567â†’        try {
   568â†’            // Get sequence info for audio export
   569â†’            const exportResult = await jsx.call('exportSequenceAudioForAnalysis');
   570â†’            if (exportResult && exportResult.success && exportResult.outputPath) {
   571â†’                audioFilePath = exportResult.outputPath;
   572â†’                console.log('[SPLICE] Audio exported to:', audioFilePath);
   573â†’            } else if (exportResult && exportResult.error) {
   574â†’                throw new Error(exportResult.error);
   575â†’            }
   576â†’        } catch (exportError) {
   577â†’            console.warn('[SPLICE] Audio export failed, trying fallback:', exportError.message);
   578â†’            // Try to get audio from first clip in timeline
   579â†’            try {
   580â†’                const clipInfo = await jsx.call('getFirstClipAudioPath');
   581â†’                if (clipInfo && clipInfo.path) {
   582â†’                    audioFilePath = clipInfo.path;
   583â†’                    console.log('[SPLICE] Using clip audio path:', audioFilePath);
   584â†’                }
   585â†’            } catch (clipError) {
   586â†’                console.warn('[SPLICE] Fallback audio path failed:', clipError.message);
   587â†’            }
   588â†’        }
   589â†’
   590â†’        if (!audioFilePath) {
   591â†’            throw new Error('Could not export or locate audio file. Please ensure sequence has audio tracks.');
   592â†’        }
   593â†’
   594â†’        // Store audio path globally for other modules
   595â†’        currentAudioPath = audioFilePath;
   596â†’        window.spliceState.audioPath = audioFilePath;
   597â†’
   598â†’        // Call backend for silence detection with audio file path
   599â†’        updateProgress(30, 'Detecting silences...');
   600â†’        const silenceResponse = await fetchWithTimeout(
   601â†’            `${getBackendUrl()}/silences-rms`,
   602â†’            {
   603â†’                method: 'POST',
   604â†’                headers: getAuthHeaders(),
   605â†’                body: JSON.stringify({
   606â†’                    wavPath: audioFilePath,
   607â†’                    sensitivity: settings.sensitivity,
   608â†’                    minSilenceLength: settings.minSilenceLength || 0.5
   609â†’                })
   610â†’            }
   611â†’        );
   612â†’
   613â†’        if (!silenceResponse.ok) {
   614â†’            throw new Error(await parseErrorResponse(silenceResponse));
   615â†’        }
   616â†’
   617â†’        const silenceData = await silenceResponse.json();
   618â†’        previewSilences = silenceData.silences || [];
   619â†’
   620â†’        // Detect takes if enabled
   621â†’        if (settings.enableTakesDetection) {
   622â†’            updateProgress(60, 'Detecting takes...');
   623â†’            const analyzeResponse = await fetchWithTimeout(
   624â†’                `${getBackendUrl()}/analyze`,
   625â†’                {
   626â†’                    method: 'POST',
   627â†’                    headers: getAuthHeaders(),
   628â†’                    body: JSON.stringify({
   629â†’                        wavPath: audioFilePath,
   630â†’                        detectTakes: true
   631â†’                    })
   632â†’                }
   633â†’            );
   634â†’
   635â†’            if (analyzeResponse.ok) {
   636â†’                const analyzeData = await analyzeResponse.json();
   637â†’                previewTakes = analyzeData.takes || [];
   638â†’
   639â†’                // Store transcript for captions/music modules
   640â†’                if (analyzeData.transcript) {
   641â†’                    currentTranscript = analyzeData.transcript;
   642â†’                    window.currentTranscript = analyzeData.transcript;
   643â†’                    window.spliceState.lastTranscript = analyzeData.transcript;
   644â†’                }
   645â†’            }
   646â†’        }
   647â†’
   648â†’        // Detect chapters if enabled
   649â†’        if (settings.enableChapters) {
   650â†’            updateProgress(80, 'Detecting chapters...');
   651â†’            try {
   652â†’                const chapterResponse = await fetchWithTimeout(
   653â†’                    `${getBackendUrl()}/chapters`,
   654â†’                    {
   655â†’                        method: 'POST',
   656â†’                        headers: getAuthHeaders(),
   657â†’                        body: JSON.stringify({
   658â†’                            wavPath: audioFilePath,
   659â†’                            transcript: currentTranscript,
   660â†’                            maxChapters: settings.maxChapters || 10,
   661â†’                            minChapterLength: settings.minChapterLength || 60
   662â†’                        })
   663â†’                    }
   664â†’                );
   665â†’
   666â†’                if (chapterResponse.ok) {
   667â†’                    const chapterData = await chapterResponse.json();
   668â†’                    currentChapters = chapterData.chapters || [];
   669â†’                }
   670â†’            } catch (e) {
   671â†’                console.warn('[SPLICE] Chapter detection failed:', e);
   672â†’            }
   673â†’        }
   674â†’
   675â†’        // Detect zoom points if enabled
   676â†’        if (settings.enableZoom) {
   677â†’            updateProgress(90, 'Detecting zoom points...');
   678â†’            try {
   679â†’                const zoomResponse = await fetchWithTimeout(
   680â†’                    `${getBackendUrl()}/zoom`,
   681â†’                    {
   682â†’                        method: 'POST',
   683â†’                        headers: getAuthHeaders(),
   684â†’                        body: JSON.stringify({
   685â†’                            wavPath: audioFilePath,
   686â†’                            transcript: currentTranscript,
   687â†’                            frequency: settings.zoomFrequency || 'medium',
   688â†’                            preset: settings.zoomPreset || 'medium',
   689â†’                            placement: settings.zoomPlacement || 'sentence_start'
   690â†’                        })
   691â†’                    }
   692â†’                );
   693â†’
   694â†’                if (zoomResponse.ok) {
   695â†’                    const zoomData = await zoomResponse.json();
   696â†’                    currentZooms = zoomData.zoomPoints || [];
   697â†’                }
   698â†’            } catch (e) {
   699â†’                console.warn('[SPLICE] Zoom detection failed:', e);
   700â†’            }
   701â†’        }
   702â†’
   703â†’        // Store results
   704â†’        lastDetectionResult = {
   705â†’            silences: previewSilences,
   706â†’            takes: previewTakes,
   707â†’            chapters: currentChapters,
   708â†’            zooms: currentZooms
   709â†’        };
   710â†’
   711â†’        // Show preview
   712â†’        hideProgress();
   713â†’        showCombinedPreview();
   714â†’        setStatus(`Found ${previewSilences.length} silences, ${previewTakes.length} takes`);
   715â†’
   716â†’    } catch (error) {
   717â†’        console.error('[SPLICE] Detection error:', error);
   718â†’        setStatus('Detection failed: ' + error.message, true);
   719â†’        hideProgress();
   720â†’    } finally {
   721â†’        isOperationInProgress = false;
   722â†’        ui.goBtn.disabled = false;
   723â†’    }
   724â†’}
