   778â†’function renderPreviewList() {
   779â†’    if (!ui.previewList) return;
   780â†’
   781â†’    const html = [];
   782â†’
   783â†’    // Render silences
   784â†’    previewSilences.forEach((silence, i) => {
   785â†’        const isSelected = selectedSilenceIndices.has(i);
   786â†’        const duration = silence.end - silence.start;
   787â†’        html.push(`
   788â†’            <div class="preview-item ${isSelected ? '' : 'excluded'}" data-index="${i}" data-type="silence">
   789â†’                <input type="checkbox" class="preview-item-check" ${isSelected ? 'checked' : ''}>
   790â†’                <div class="preview-item-info">
   791â†’                    <div class="preview-item-time">${formatTime(silence.start)} - ${formatTime(silence.end)}</div>
   792â†’                    <div class="preview-item-duration">${duration.toFixed(2)}s silence</div>
   793â†’                </div>
   794â†’            </div>
   795â†’        `);
   796â†’    });
   797â†’
   798â†’    // Render takes
   799â†’    previewTakes.forEach((take, i) => {
   800â†’        // SECURITY: Escape take label to prevent XSS
   801â†’        const safeLabel = escapeHtml(take.label || 'Take ' + (take.takeNumber || i + 1));
