   267â†’/**
   268â†’ * Handle text changes in editor
   269â†’ */
   270â†’function handleTextChange(newText) {
   271â†’  // Save current state to undo stack
   272â†’  if (textEditorState.currentText !== newText) {
   273â†’    textEditorState.undoStack.push(textEditorState.currentText);
   274â†’    textEditorState.redoStack = []; // Clear redo stack on new edit
   275â†’
   276â†’    // Limit undo stack size
   277â†’    if (textEditorState.undoStack.length > MAX_UNDO_STACK) {
   278â†’      textEditorState.undoStack.shift();
   279â†’    }
   280â†’  }
   281â†’
   282â†’  textEditorState.currentText = newText;
   283â†’  textEditorState.isDirty = true;
   284â†’  textEditorState.previewChanges = null; // Clear preview
   285â†’
   286â†’  updateEditorStatus();
   287â†’}
