   289â†’/**
   290â†’ * Undo last text edit
   291â†’ */
   292â†’function undoTextEdit() {
   293â†’  if (textEditorState.undoStack.length === 0) {
   294â†’    showTextEditorStatus('Nothing to undo', 'info');
   295â†’    return;
   296â†’  }
   297â†’
   298â†’  // Save current to redo stack
   299â†’  textEditorState.redoStack.push(textEditorState.currentText);
   300â†’
   301â†’  // Restore from undo stack
   302â†’  textEditorState.currentText = textEditorState.undoStack.pop();
   303â†’
   304â†’  // Update editor content
   305â†’  const editor = textEditorElements.content || document.getElementById('text-editor-content');
   306â†’  if (editor) {
   307â†’    editor.value = textEditorState.currentText;
   308â†’  }
   309â†’
   310â†’  updateEditorStatus();
   311â†’  showTextEditorStatus('Undo applied', 'info');
   312â†’}
