   128â†’
   129â†’// ============================================================================
   130â†’// EVENT LISTENERS
   131â†’// ============================================================================
   132â†’function setupCaptionEventListeners() {
   133â†’    // Template gallery delegation
   134â†’    if (captionsUI.templateGallery) {
   135â†’        captionsUI.templateGallery.addEventListener('click', (e) => {
