   151â†’    }
   152â†’    if (captionsUI.exportMogrtBtn) {
   153â†’        captionsUI.exportMogrtBtn.addEventListener('click', () => exportCaptions('mogrt'));
   154â†’    }
   155â†’
   156â†’    // Apply to timeline button
   157â†’    if (captionsUI.applyBtn) {
   158â†’        captionsUI.applyBtn.addEventListener('click', applyCaptionsToTimeline);
   159â†’    }
   160â†’
   161â†’    // Copy button
