   169â†’            animatedCaptionsState.settings.maxWordsPerLine = parseInt(e.target.value) || 4;
   170â†’        });
   171â†’    }
   172â†’    if (captionsUI.highlightKeywords) {
   173â†’        captionsUI.highlightKeywords.addEventListener('change', (e) => {
   174â†’            animatedCaptionsState.settings.highlightKeywords = e.target.checked;
   175â†’        });
   176â†’    }
   177â†’    if (captionsUI.insertEmojis) {
   178â†’        captionsUI.insertEmojis.addEventListener('change', (e) => {
   179â†’            animatedCaptionsState.settings.insertEmojis = e.target.checked;
   180â†’            // Toggle emoji frequency group visibility
