   186â†’    }
   187â†’    if (captionsUI.emojiFrequency) {
   188â†’        captionsUI.emojiFrequency.addEventListener('change', (e) => {
   189â†’            animatedCaptionsState.settings.emojiFrequency = e.target.value;
