   192â†’}
   193â†’
   194â†’// ============================================================================
   195â†’// TEMPLATE GALLERY
   196â†’// ============================================================================
   197â†’function renderTemplateGallery() {
   198â†’    if (!captionsUI.templateGallery) return;
   199â†’
   200â†’    const fragment = document.createDocumentFragment();
   201â†’
   202â†’    animatedCaptionsState.templates.forEach(template => {
   203â†’        const card = document.createElement('div');
   204â†’        card.className = `caption-template-card ${animatedCaptionsState.selectedTemplate === template.id ? 'selected' : ''}`;
   205â†’        card.dataset.templateId = template.id;
