   231â†’
   232â†’function selectCaptionTemplate(templateId) {
   233â†’    animatedCaptionsState.selectedTemplate = templateId;
   234â†’
   235â†’    // Update UI
   236â†’    document.querySelectorAll('.caption-template-card').forEach(card => {
