   240â†’    console.log(`[SPLICE Captions] Selected template: ${templateId}`);
   241â†’
   242â†’    // Re-apply template if we have captions
   243â†’    if (animatedCaptionsState.captions) {
   244â†’        applyTemplateToExisting(templateId);
   245â†’    }
   246â†’}
   247â†’
   248â†’async function applyTemplateToExisting(templateId) {
   249â†’    if (!animatedCaptionsState.captions) return;
   250â†’
   251â†’    try {
   252â†’        const apiUrl = getBackendUrl();
   253â†’        const response = await fetchWithTimeout(`${apiUrl}/captions/apply-template`, {
   254â†’            method: 'POST',
   255â†’            headers: getAuthHeaders(),
   256â†’            body: JSON.stringify({
   257â†’                captionData: { captions: animatedCaptionsState.captions },
   258â†’                templateId
   259â†’            })
   260â†’        }, 30000);
   261â†’
   262â†’        if (response.ok) {
   263â†’            const data = await response.json();
   264â†’            animatedCaptionsState.captions = data.captions;
   265â†’            renderCaptionPreview();
   266â†’        }
   267â†’    } catch (err) {
   268â†’        console.error('[SPLICE Captions] Failed to apply template:', err);
   269â†’    }
   270â†’}
   271â†’
   272â†’// ============================================================================
   273â†’// CAPTION GENERATION
   274â†’// ============================================================================
   275â†’async function generateCaptions() {
   276â†’    const transcript = getLastTranscript();
   277â†’    if (!transcript) {
   278â†’        showCaptionStatus('No transcript available. Run transcription first.', 'error');
   279â†’        return;
   280â†’    }
   281â†’
   282â†’    // Check online status
   283â†’    if (typeof isOnline === 'function' && !isOnline()) {
   284â†’        showCaptionStatus('Offline - Check your connection', 'error');
   285â†’        return;
   286â†’    }
   287â†’
   288â†’    animatedCaptionsState.isGenerating = true;
   289â†’    updateGenerateButton(true);
   290â†’    showCaptionStatus('Generating animated captions...', 'info');
   291â†’
   292â†’    try {
   293â†’        const apiUrl = getBackendUrl();
   294â†’        const response = await fetchWithTimeout(`${apiUrl}/captions/animate`, {
   295â†’            method: 'POST',
   296â†’            headers: getAuthHeaders(),
   297â†’            body: JSON.stringify({
   298â†’                transcript,
   299â†’                template: animatedCaptionsState.selectedTemplate,
   300â†’                settings: animatedCaptionsState.settings
   301â†’            })
   302â†’        }, 120000);
   303â†’
   304â†’        if (!response.ok) {
   305â†’            const errorMsg = await parseErrorResponse(response);
   306â†’            throw new Error(errorMsg);
   307â†’        }
   308â†’
   309â†’        const data = await response.json();
   310â†’        animatedCaptionsState.captions = data.captions;
   311â†’        animatedCaptionsState.keywords = data.keywords || [];
   312â†’
   313â†’        // Update UI
   314â†’        renderCaptionPreview();
   315â†’        updateCaptionStats(data);
   316â†’        showCaptionExportActions(true);
   317â†’        showCaptionStatus(`Generated ${data.totalCaptions} caption lines`, 'success');
   318â†’
   319â†’    } catch (err) {
   320â†’        console.error('[SPLICE Captions] Generation error:', err);
   321â†’        showCaptionStatus(err.message, 'error');
   322â†’    } finally {
   323â†’        animatedCaptionsState.isGenerating = false;
   324â†’        updateGenerateButton(false);
   325â†’    }
   326â†’}
   327â†’
   328â†’function getLastTranscript() {
   329â†’    // Try main module state
   330â†’    if (window.spliceState?.lastTranscript) {
   331â†’        return window.spliceState.lastTranscript;
   332â†’    }
   333â†’    // Try local state
   334â†’    if (animatedCaptionsState.lastTranscript) {
   335â†’        return animatedCaptionsState.lastTranscript;
   336â†’    }
   337â†’    return null;
   338â†’}
   339â†’
   340â†’function setCaptionTranscript(transcript) {
   341â†’    animatedCaptionsState.lastTranscript = transcript;
   342â†’}
   343â†’
   344â†’// ============================================================================
   345â†’// CAPTION PREVIEW
   346â†’// ============================================================================
   347â†’function renderCaptionPreview() {
   348â†’    if (!captionsUI.previewContainer || !animatedCaptionsState.captions) return;
   349â†’
   350â†’    const captions = animatedCaptionsState.captions.slice(0, 10);
   351â†’    const fragment = document.createDocumentFragment();
   352â†’
   353â†’    const list = document.createElement('div');
   354â†’    list.className = 'caption-preview-list';
   355â†’
   356â†’    captions.forEach((caption, i) => {
   357â†’        const item = document.createElement('div');
   358â†’        item.className = 'caption-preview-item';
   359â†’        item.dataset.index = i;
   360â†’
   361â†’        const hasKeywords = caption.words?.some(w => w.isKeyword);
   362â†’        item.innerHTML = `
   363â†’            <span class="caption-time">${formatCaptionTime(caption.start)}</span>
   364â†’            <span class="caption-text ${hasKeywords ? 'has-keywords' : ''}">${formatCaptionText(caption)}</span>
   365â†’        `;
   366â†’
   367â†’        list.appendChild(item);
   368â†’    });
   369â†’
   370â†’    if (animatedCaptionsState.captions.length > 10) {
   371â†’        const more = document.createElement('div');
   372â†’        more.className = 'caption-preview-more';
   373â†’        more.textContent = `+ ${animatedCaptionsState.captions.length - 10} more captions`;
   374â†’        list.appendChild(more);
   375â†’    }
   376â†’
   377â†’    fragment.appendChild(list);
   378â†’
   379â†’    captionsUI.previewContainer.innerHTML = '';
   380â†’    captionsUI.previewContainer.appendChild(fragment);
   381â†’}
   382â†’
   383â†’// SECURITY: Escape HTML to prevent XSS
   384â†’function escapeHtmlCaptions(str) {
   385â†’    if (!str) return '';
   386â†’    const div = document.createElement('div');
   387â†’    div.textContent = str;
   388â†’    return div.innerHTML;
   389â†’}
   390â†’
   391â†’function formatCaptionText(caption) {
   392â†’    if (!caption.words) return escapeHtmlCaptions(caption.text);
   393â†’
   394â†’    // SECURITY: Escape user-provided word content to prevent XSS
   395â†’    return caption.words.map(word => {
   396â†’        const safeWord = escapeHtmlCaptions(word.word);
   397â†’        if (word.isKeyword) {
   398â†’            return `<span class="caption-keyword">${safeWord}</span>`;
   399â†’        }
   400â†’        if (word.emoji) {
   401â†’            const safeEmoji = escapeHtmlCaptions(word.emoji);
   402â†’            return `${safeWord} ${safeEmoji}`;
   403â†’        }
   404â†’        return safeWord;
   405â†’    }).join(' ');
   406â†’}
   407â†’
   408â†’function formatCaptionTime(seconds) {
   409â†’    const mins = Math.floor(seconds / 60);
   410â†’    const secs = Math.floor(seconds % 60);
   411â†’    return `${mins}:${secs.toString().padStart(2, '0')}`;
   412â†’}
   413â†’
   414â†’function updateCaptionStats(data) {
   415â†’    if (!captionsUI.stats) return;
   416â†’
   417â†’    captionsUI.stats.innerHTML = `
   418â†’        <span class="stat-item">${data.totalCaptions} lines</span>
   419â†’        <span class="stat-item">${data.totalWords} words</span>
   420â†’        <span class="stat-item">${data.keywords?.length || 0} keywords</span>
   421â†’        <span class="stat-item">${formatCaptionTime(data.duration)}</span>
   422â†’    `;
   423â†’}
   424â†’
   425â†’// ============================================================================
   426â†’// EXPORT FUNCTIONS
   427â†’// ============================================================================
   428â†’async function exportCaptions(format) {
   429â†’    if (!animatedCaptionsState.captions) {
   430â†’        showCaptionStatus('No captions to export. Generate captions first.', 'error');
   431â†’        return;
   432â†’    }
   433â†’
   434â†’    showCaptionStatus(`Exporting ${format.toUpperCase()}...`, 'info');
   435â†’
   436â†’    try {
   437â†’        const apiUrl = getBackendUrl();
   438â†’        let endpoint, body;
   439â†’
