   325â†’  socialReframeState.isAnalyzing = true;
   326â†’  updateAnalyzeButton(true);
   327â†’  showReframeStatus('Analyzing video for face tracking...', 'info');
   328â†’
   329â†’  try {
   330â†’    const backendUrl = typeof getBackendUrl === 'function' ? getBackendUrl() : 'https://127.0.0.1:3847';
   331â†’    const headers = typeof getAuthHeaders === 'function' ? getAuthHeaders() : { 'Content-Type': 'application/json' };
   332â†’    const fetchFn = typeof fetchWithTimeout === 'function' ? fetchWithTimeout : fetch;
   333â†’
   334â†’    const slider = reframeElements.smoothingSlider || document.getElementById('motion-smoothing-slider');
   335â†’    const smoothing = slider?.value || 0.3;
   336â†’
   337â†’    const response = await fetchFn(`${backendUrl}/reframe/analyze`, {
