   395â†’
   396â†’/**
   397â†’ * Render analysis results
   398â†’ */
   399â†’function renderAnalysisResults(analysis) {
   400â†’  const container = reframeElements.analysisResults || document.getElementById('reframe-analysis-results');
   401â†’  if (!container) return;
   402â†’
   403â†’  // SECURITY: Escape analysis data to prevent XSS
   404â†’  const html = `
   405â†’    <div class="analysis-summary">
   406â†’      <div class="analysis-stat">
   407â†’        <span class="stat-label">Faces Found</span>
