   440â†’
   441â†’// ============================================================================
   442â†’// PREVIEW
   443â†’// ============================================================================
   444â†’
   445â†’/**
   446â†’ * Update crop preview canvas
   447â†’ */
   448â†’function updateCropPreview() {
   449â†’  const canvas = reframeElements.previewCanvas || document.getElementById('reframe-preview-canvas');
   450â†’  if (!canvas || !socialReframeState.analysis) return;
   451â†’
   452â†’  const ctx = canvas.getContext('2d');
   453â†’  const { videoInfo, suggestions } = socialReframeState.analysis;
   454â†’  const aspectId = socialReframeState.selectedAspectRatio;
   455â†’  const suggestion = suggestions[aspectId];
   456â†’
   457â†’  if (!suggestion) return;
   458â†’
   459â†’  // Clear canvas
   460â†’  ctx.clearRect(0, 0, canvas.width, canvas.height);
   461â†’
   462â†’  // Draw video frame representation
   463â†’  const scale = Math.min(canvas.width / videoInfo.width, canvas.height / videoInfo.height);
