   477â†’    ctx.lineWidth = 2;
   478â†’    ctx.strokeRect(
   479â†’      videoX + crop.x * videoW,
   480â†’      videoY + crop.y * videoH,
   481â†’      crop.width * videoW,
   482â†’      crop.height * videoH
   483â†’    );
   484â†’
   485â†’    // Draw face indicator
   486â†’    if (socialReframeState.analysis.faceTracking.tracks[0]) {
   487â†’      const face = socialReframeState.analysis.faceTracking.tracks[0].positions[0];
   488â†’      if (face) {
   489â†’        ctx.fillStyle = 'rgba(255, 0, 0, 0.3)';
   490â†’        ctx.fillRect(
   491â†’          videoX + face.x * videoW,
   492â†’          videoY + face.y * videoH,
   493â†’          face.width * videoW,
   494â†’          face.height * videoH
   495â†’        );
   496â†’      }
   497â†’    }
   498â†’  }
   499â†’
   500â†’  // Draw safe zones if enabled
   501â†’  if (socialReframeState.showSafeZones && socialReframeState.safeZones) {
   502â†’    renderSafeZoneOnCanvas(ctx, videoX, videoY, videoW, videoH);
   503â†’  }
   504â†’}
   505â†’
   506â†’/**
   507â†’ * Render safe zone overlay on canvas
   508â†’ */
   509â†’function renderSafeZoneOnCanvas(ctx, x, y, width, height) {
   510â†’  const sz = socialReframeState.safeZones.safeZone;
   511â†’  if (!sz) return;
   512â†’
   513â†’  ctx.fillStyle = 'rgba(255, 0, 0, 0.15)';
   514â†’
   515â†’  // Top safe zone
   516â†’  ctx.fillRect(x, y, width, height * sz.top);
   517â†’
   518â†’  // Bottom safe zone
   519â†’  ctx.fillRect(x, y + height * (1 - sz.bottom), width, height * sz.bottom);
