   437â†’        for (let i = 1; i < speakerConfig.length; i++) {
   438â†’            audioPaths.push(audioPath);
   439â†’        }
   440â†’
   441â†’        const settings = getMultitrackSettings();
   442â†’        const apiUrl = getBackendUrl();
   443â†’
   444â†’        const response = await fetchWithTimeout(`${apiUrl}/multitrack/auto-balance`, {
   445â†’            method: 'POST',
   446â†’            headers: getAuthHeaders(),
   447â†’            body: JSON.stringify({
   448â†’                audioPaths,
   449â†’                speakerNames: settings.speakerNames,
   450â†’                videoTrackMapping: settings.videoTrackMapping,
   451â†’                wideShotEnabled: settings.wideShotEnabled,
   452â†’                wideShotPercentage: settings.wideShotPercentage,
   453â†’                wideShotTracks: settings.wideShotTracks,
   454â†’                minShotDuration: settings.minShotDuration,
   455â†’                switchingFrequency: settings.switchingFrequency
   456â†’            })
   457â†’        }, 120000);
   458â†’
   459â†’        if (!response.ok) {
   460â†’            const errorMsg = await parseErrorResponse(response);
